import Box from '@material-ui/core/Box';
import Select from '@material-ui/core/Select';
import { height, width } from "dom-helpers";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={4}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}));

var number = 123456789;
console.log(new Intl.NumberFormat('de-DE').format(number));
console.log(new Intl.NumberFormat('ar-EG').format(number));
console.log(new Intl.NumberFormat('zh-Hans-CN-u-nu-hanidec').format(number));
console.log(new Intl.NumberFormat('th-TH').format(number));
const materialTheme = createMuiTheme({
  overrides: {
    MuiPickersToolbar: {
      toolbar: {
        backgroundColor: "#1c2b36",
      },
    },
    MuiPickersCalendarHeader: {
      switchHeader: {
        backgroundColor: "black",
        color: "white",
      },
      dayLabel: {
        color: "#61dafb",
      },
      iconButton: {
        backgroundColor: "#2eaccf",
      },
    },
    MuiPaper: {
      root: {
        color: "white",
        backgroundColor: "#242f39"
      },
    },
    MuiPickersStaticWrapper: {
      staticWrapperRoot: {
        backgroundColor: "#1c2b36",
      },
    },
    MuiIconButton: {
      root: {
        color: "gray",
        padding: "5px",
      },
      label: {
        name: ""
      }
    },
    MuiSvgIcon: {
      root: {
        fontSize: "1rem",
      },
    },
    MuiInputBase: {
      input: {
        color: "white",
        background: "#223542",
        borderRadius: "10px",
      },
    },
    MuiButton: {
      textPrimary: {
        color: "#20bcd7",
      },
    },
    MuiListItem: {
      root: {
        "&$selected": {
          backgroundColor: "#2a8697",
          color: "black",
        },
        "&:hover": {
          backgroundColor: "#54656a96",
        },
      },
    },
    MuiMenuItem: {
      root: {
        "&:hover": {
          backgroundColor: "#54656a96",
        },
      },
    },
  },
});

export const styles = makeStyles(() => ({

  notInThisMonthDayPaper: {
    width: "35px",
    height: "35px",
    backgroundColor: "#0000003d",
    margin: "3px",
    boxShadow: "none",
    borderRadius: 0,
    padding: "1px",
  },
  normalDayPaper: {
    width: "35px",
    height: "35px",
    backgroundColor: "#54656a52",
    margin: "3px",
    // boxShadow: "none",
    // borderRadius: 0,
    padding: "1px",
    cursor: "pointer",
  },
  selectedDayPaper: {
    width: "31px",
    height: "31px",
    backgroundColor: "#3593af6b",
    margin: "3px",
    boxShadow: "none",
    borderRadius: 0,
    borderStyle: "solid",
    borderWidth: "2px",
    borderColor: "#20bcd7",
    padding: "1px",
    cursor: "pointer",
  },
  todayPaper: {
    width: "35px",
    height: "35px",
    backgroundColor: "#fff1cbcf",
    margin: "3px",
    boxShadow: "none",
    borderRadius: 0,
    padding: "1px",
    cursor: "pointer",
    color: "#1c2b36",
  },
  notInThisMonthDayPaper5: {
    width: "35px",
    height: "35px",
    margin: "3px",
    padding: "1px",
    boxShadow: "none",
    // borderRadius: 0,
    backgroundColor: "#0000003d",
  },
  timezoneSelect: {
    // backgroundColor: "#223542",
    // padding: "20px",
    // margin: "20px auto",
    borderRadius: "5px",
    maxWidth: "600px",
    margin: "0 20px",
    fontWeight: 500,
    fontFamily: "tahoma",
    color: "black",
  },
  dialog:{
    "&.MuiDialog-paperFullWidth":{
      width: "500rem",
      height:'100%'

    }
  },
}));

let ScoreChartTemplate = ({ startDate, endDate }) => {


  // Generate data for chart
  let base = +new Date(2021, 1, 1);
  let oneDay = 24 * 3600 * 1000;
  let date = [];
  let data = [Math.random() * 300];
  for (let i = 1; i < 20000; i++) {
    var now = new Date((base += oneDay));
    date.push([now.getFullYear(), now.getMonth() + 1, now.getDate()].join('/'));
    data.push(Math.round((Math.random() - 0.5) * 20 + data[i - 1]));
  }

  return {

    //Area Chart
    tooltip: {
      trigger: 'axis',
      position: function (pt) {
        return [pt[0], '10%'];
      }
    },
    title: {
      left: 'left',
      text: 'Test Chart'
    },
    toolbox: {
      feature: {
        dataZoom: {
          yAxisIndex: 'none'
        },
        restore: {},
        saveAsImage: {}
      }
    },
    xAxis: {
      type: 'category',
      boundaryGap: false,
      data: date,
      min: startDate,
      max: endDate
    },
    yAxis: {
      type: 'value',
      boundaryGap: [0, '100%']
    },
    dataZoom: [
      {
        type: 'inside',
        start: 0,
        end: 10
      },
      {
        start: 0,
        end: 10
      }
    ],
    series: [
      {
        name: 'Test Data',
        type: 'line',
        symbol: 'none',
        sampling: 'lttb',
        itemStyle: {
          color: 'rgb(255, 70, 131)'
        },
        areaStyle: {
          color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [
            {
              offset: 0,
              color: 'rgb(255, 158, 68)'
            },
            {
              offset: 1,
              color: 'rgb(255, 70, 131)'
            }
          ])
        },
        data: data,
      }

    ]
  }
}

export default function CustomCalendar() {
  const classes = styles();
  const today = new Date();
  const sunnyDays = [1, 6, 10, 24, 15]
  const cloudyDays = [30, 4, 13, 21]
  const snowyDays = [25, 3, 12, 11, 27]
  const [startDate, setStartDate] = useState(new Date())
  const [endDate, setEndDate] = useState(new Date())
  // const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };


  function getDayElement(day, selectedDate, isInCurrentMonth, dayComponent) {
    const isSunny = sunnyDays.includes(day.getDate());
    const isCloudy = cloudyDays.includes(day.getDate());
    const isSnow = snowyDays.includes(day.getDate());
    const isSelected = day.getDate() === selectedDate.getDate();
    const isToday = day.getDate() === today.getDate() && day.getMonth() === today.getMonth();
    console.log(day.getTime())
    let dateTile
    if (isInCurrentMonth) {
      if (isSunny) {
        dateTile = (
          <Paper
            className={
              isSelected ? classes.selectedDayPaper : isToday ? classes.todayPaper : classes.normalDayPaper
            }
          >
            <Grid item>
              <WbSunnyIcon style={{ color: "orange" }} />
            </Grid>
            <Grid item>
              {day.getDate()}
            </Grid>
          </Paper>)
      } else if (isCloudy) {
        dateTile = (<Paper
          className={isSelected ? classes.selectedDayPaper : isToday ? classes.todayPaper : classes.normalDayPaper}
        >
          <Grid item>
            <CloudIcon style={{ color: "gray" }} />

          </Grid>
          <Grid item> {day.getDate()}</Grid>
        </Paper>)
      } else if (isSnow) {
        dateTile = (
          <Paper
            className={isSelected ? classes.selectedDayPaper : isToday ? classes.todayPaper : classes.normalDayPaper}
          >
            <Grid item>
              <AcUnitIcon style={{ color: "#3d5afe" }} />
            </Grid>
            <Grid item> {day.getDate()}</Grid>
          </Paper>
        )
      } else {
        dateTile = (<Paper
          className={isSelected ? classes.selectedDayPaper : isToday ? classes.todayPaper : classes.normalDayPaper}
        >
          <Grid item>
            <br />
          </Grid>
          <Grid item> {day.getDate()}</Grid>
        </Paper>)
      }

    } else {
      dateTile = (<Paper className={classes.notInThisMonthDayPaper}>
        <Grid item>
          <br />
        </Grid>
        <Grid item style={{ color: "lightGrey" }}>
          {day.getDate()}
        </Grid>
      </Paper>)
    }
    return dateTile
  }
  const localeMap = {
    en: enLocale,
    fr: frLocale,
    ru: ruLocale,
    ja: jaLocale,
    ko: koLocale,
    zh: zhLocale,
    th: thLocale,
    ar: arLocale,
    he: heLocale,
  };
  class RuLocalizedUtils extends DateFnsUtils {
    getCalendarHeaderText(date) {
      return format(date, "LLLL", { locale: this.locale });
    }
    getDatePickerHeaderText(date) {
      return format(date, "EEEE dd MMMM yyyy", { locale: this.locale });
    }
  }
  class FrLocalizedUtils extends DateFnsUtils {
    getDatePickerHeaderText(date) {
      return format(date, "d MMM yyyy", { locale: this.locale });
    }
