    }
    getDatePickerHeaderText(date) {
      return format(date, "EEEE d MMMM yyyy", { locale: this.locale });
    }
  }
  class KoLocalizedUtils extends DateFnsUtils {
    getCalendarHeaderText(date) {
      return format(date, "LLLL", { locale: this.locale });
    }
    getDatePickerHeaderText(date) {
      return format(date, "EEEE d MMMM yyyy", { locale: this.locale });
    }
  }
  class ZhLocalizedUtils extends DateFnsUtils {
    getCalendarHeaderText(date) {
      return format(date, "LLLL", { locale: this.locale });
    }
    getDatePickerHeaderText(date) {
      return format(date, "EEEE d MMMM yyyy", { locale: this.locale });
    }
  }
  class ArLocalizedUtils extends DateFnsUtils {
    getCalendarHeaderText(date) {
      return format(date, "LLLL", { locale: this.locale });
    }
    getDatePickerHeaderText(date) {
      return format(date, "EEEE d MMMM yyyy", { locale: this.locale });
    }
  }
  class HeLocalizedUtils extends DateFnsUtils {
    getCalendarHeaderText(date) {
      return format(date, "LLLL", { locale: this.locale });
    }
    getDatePickerHeaderText(date) {
      return format(date, "EEEE d MMMM yyyy", { locale: this.locale });
    }
  }
  const localeUtilsMap = {
    en: DateFnsUtils,
    fr: FrLocalizedUtils,
    ru: RuLocalizedUtils,
    ja: JaLocalizedUtils,
    ko: KoLocalizedUtils,
    zh: ZhLocalizedUtils,
    th: ThLocalizedUtils,
    ar: ArLocalizedUtils,
    he: HeLocalizedUtils,

  };
  const [selectedTimezone, setSelectedTimezone] = useState({})
  const [locale, setLocale] = useState("en");
  const [anchorEl, setAnchorEl] = useState(null);

  const handleMenuOpen = useCallback(e => {
    //e.stopPropagation();
    console.log("e.currentTarget;;", e.currentTarget)
    setAnchorEl(e.currentTarget);
  }, []);

  const selectLocale = useCallback(locale => {
    setLocale(locale);
    setAnchorEl(null);
  }, []);

  const localeFormatMap = {
    en: "MMMM d, yyyy",
    fr: "d MMM yyyy",
    ru: "d MMM yyyy",
    ja: "yyyy MMM d",
    ko: "yyyy MMM d",
    zh: "yyyy MMM d",
